<div id="canvascontainer"></div><!--kg-card-end: html--><!--kg-card-begin: html--><script>
    //Variables
    var myGameArea;
    var myGamePiece;
    var myObstacles = [];
    var myscore;
    var myvelocidad;
    var aumentar = 0;
    var lleva = 1;
    var fotoK = "https://i.ibb.co/Q99TmLL/puesta-en-marcha.png";

    function restartGame() {
        //Cuando se falla muestra un cuadrado oscuro transparente
        document.getElementById("myfilter").style.display = "none";
        //Se reinicia el juego
        document.getElementById("myrestartbutton").style.display = "none";
        //Detiene el area del juego
        myGameArea.stop();
        //Limpia el area del juego, para que no se presenten problemas de bug
        myGameArea.clear();
        //Asigna valor vacio
        myGameArea = {};
        myGamePiece = {};
        myObstacles = [];
        myscore = {};
        myvelocidad = {};
        this.aumentar = 0;
        this.lleva = 1;
        document.getElementById("canvascontainer").innerHTML = "";
        startGame()
    }

    function startGame() {
        //Instanciamos
        myGameArea = new gamearea();
        //Asignamos los parametros del cuadrado
        myGamePiece = new component(30, 30, fotoK, 10, 75, "image");
        //Asiganmos los parametros del puntaje
        myscore = new component("15px", "Consolas", "white", 200, 25, "text");
        //Velocidad componente
        myvelocidad = new component("15px", "Consolas", "white", 180, 50, "text");
        myGameArea.start();
    }

    function gamearea() {
        //Establacemos el canvas
        this.canvas = document.createElement("canvas");
        //Asignamos el ancho
        this.canvas.width = 320;
        //Asignamos la altura
        this.canvas.height = 180;
        document.getElementById("canvascontainer").appendChild(this.canvas);
        //Establecemos el contexto en dos dimensiones
        this.context = this.canvas.getContext("2d");
        this.pause = false;
        this.frameNo = 0;
        //Habilitar teclado
        window.addEventListener('keydown', function (e) {
            e.preventDefault();
            myGameArea.keys = (myGameArea.keys || []);
            myGameArea.keys[e.keyCode] = (e.type == "keydown");
        })
        window.addEventListener('keyup', function (e) {
            myGameArea.keys[e.keyCode] = (e.type == "keydown");
        })
        //Intervalo de tiempo
        this.start = function () {
            this.interval = setInterval(updateGameArea, 20);
        }
        //Detiene el juego
        this.stop = function () {
            clearInterval(this.interval);
            this.pause = true;
        }
        //Limpia el juego
        this.clear = function () {
            this.context.clearRect(0, 0, this.canvas.width, this.canvas.height);
        }
    }

    function component(width, height, color, x, y, type) {
        //Asigna el tipo
        this.type = type;
        //Si es una imagen
        if (type == "image") {
            this.image = new Image();
            this.image.src = color;
        }
        //Si la variable text existe, se tomara en cuenta su valor
        if (type == "text") {
            this.text = color;
        }
        //Inicio del puntaje
        this.score = 0;
        //Dimensiones
        this.width = width;
        this.height = height;
        //La velocidad en que se mueve
        this.speedX = 0;
        this.speedY = 0;
        //Posiciones en el plano
        this.x = x;
        this.y = y;
        //Gravedad
        this.gravity = 0.05;
        this.gravitySpeed = 0;
        this.bounce = 0.6;
        this.update = function () {
            ctx = myGameArea.context;
            if (this.type == "text") {
                ctx.font = this.width + " " + this.height;
                ctx.fillStyle = color;
                ctx.fillText(this.text, this.x, this.y);
            } else if (type == "image") {
                ctx.drawImage(this.image,
                    this.x,
                    this.y,
                    this.width, this.height);
            }
            else {
                ctx.fillStyle = color;
                ctx.fillRect(this.x, this.y, this.width, this.height);
                ctx.strokeRect(this.x, this.y, this.width, this.height);
            }
        }
        //Cuando choca con una figura
        this.crashWith = function (otherobj) {
            var myleft = this.x;
            var myright = this.x + (this.width);
            var mytop = this.y;
            var mybottom = this.y + (this.height);
            var otherleft = otherobj.x;
            var otherright = otherobj.x + (otherobj.width);
            var othertop = otherobj.y;
            var otherbottom = otherobj.y + (otherobj.height);
            var crash = true;
            if ((mybottom < othertop) || (mytop > otherbottom) || (myright < otherleft) || (myleft > otherright)) {
                crash = false;
            }
            return crash;
        }
        //Para el desplazamiento
        this.newPos = function () {
            this.gravitySpeed += this.gravity;
            this.x += this.speedX;
            this.y += this.speedY + this.gravitySpeed;
            this.hitBottom();
            this.hittecho();
        }

        //Para que caiga en el borde
        this.hitBottom = function () {
            var rockbottom = myGameArea.canvas.height - this.height;
            if (this.y > rockbottom) {
                this.y = rockbottom;
                this.gravitySpeed = -(this.gravitySpeed * this.bounce);
            }
        }

        //Para que caiga en el borde
        this.hittecho = function () {
            var rockbottom = 0;
            if (-this.y > rockbottom) {
                this.y = rockbottom;
                this.gravitySpeed = +(this.gravitySpeed * this.bounce);
            }
        }
    }

    function updateGameArea() {
        var x, y, min, max, height, gap;
        for (i = 0; i < myObstacles.length; i += 1) {
            if (myGamePiece.crashWith(myObstacles[i])) {
                myGameArea.stop();
                document.getElementById("myfilter").style.display = "block";
                document.getElementById("myrestartbutton").style.display = "block";
                return;
            }
        }
        if (myGameArea.pause == false) {
            myGameArea.clear();
            myGameArea.frameNo += 1;
            myscore.score += 1;
            if (myGameArea.frameNo == 1 || everyinterval(150)) {
                x = myGameArea.canvas.width;
                y = myGameArea.canvas.height - 100;
                min = 20;
                max = 100;
                height = Math.floor(Math.random() * (max - min + 1) + min);
                min = 50;
                max = 100;
                gap = Math.floor(Math.random() * (max - min + 1) + min);
                //Obstaculos superiores
                myObstacles.push(new component(10, height, "red", x, 0));
                //Obstaculos inferiores
                myObstacles.push(new component(10, x - height - gap, "yellow", x, height + gap));
            }

            //Velocidad condicion
            if (myGameArea.frameNo % 500 == 0) {
                //aumentar = 0.2;
                aumentar = lleva * 0.2;
            }
            else {
                aumentar = 0;
            }
            aumentar.toFixed(2);
            lleva = lleva + aumentar;

            myvelocidad.text = "Velocidad: " + lleva.toFixed(2);
            myvelocidad.update();

            for (i = 0; i < myObstacles.length; i += 1) {
                myObstacles[i].x += -lleva;
                myObstacles[i].update();
            }

            //Mensaje que dice puntaje
            myscore.text = "Puntaje: " + myscore.score;
            myscore.update();

            //Permitir que pueda mover
            myGamePiece.speedX = 0;
            myGamePiece.speedY = 0;

            //Arriba
            if ((myGameArea.keys && myGameArea.keys[32]) || (myGameArea.keys && myGameArea.keys[38])) {
                accelerate(-0.2);
            }
            else {
                accelerate(0.1);
            }
            myGamePiece.newPos();
            myGamePiece.update();
        }
    }

    function accelerate(n) {
        myGamePiece.gravity = n;
    }

    function everyinterval(n) {
        if ((myGameArea.frameNo / n) % 1 == 0) { return true; }
        return false;
    }

    function clearmove(e) {
        myGamePiece.speedX = 0;
        myGamePiece.speedY = 0;
    }
    startGame();

