<html>

<head>
    <meta charset="utf-8">
    <title>Figura rombo</title>
</head>

<body>
    <script type="text/javascript">
        function mifig() {
            var maximo = document.getElementById("numero").value;
            var guardar = "";
            if (maximo >= 0 && maximo <= 9) {
                //document.write("Rombo con el numero " + maximo);
                guardar+=maximo;
                //document.write("<pre>")
                guardar+="<pre>";
                maximo = (maximo * 2) - 1;
                //Piramide superior
                for (var fila = 1; fila <= maximo; fila++) {
                    if (fila % 2 != 0) {
                        //Piramide de mayor a menor
                        for (var columna = maximo; columna >= fila; columna--) {
                            //document.write("&nbsp;");
                            guardar+="&nbsp;"
                        }
                        //Piramide de menor a mayor
                        for (var colsup = 1; colsup <= fila; colsup++) {
                            if (colsup == 1 || colsup == fila) {
                                //document.write("&#42;&nbsp;");
                                guardar+="&#42;&nbsp;";
                            } else if (fila == maximo && colsup == Math.trunc(maximo / 2) + 1) {
                                //document.write("&#43;&nbsp;");
                                guardar+="&#43;&nbsp;";
                            } else if (fila == maximo) {
                                //document.write("&#45;&nbsp;");
                                guardar+="&#45;&nbsp;";
                            } else if (colsup == (Math.trunc(fila / 2) + 1)) {
                                //document.write("&#124;&nbsp;");
                                guardar+="&#124;&nbsp;";
                            } else {
                                //document.write("&nbsp;&nbsp;");
                                guardar+="&nbsp;&nbsp;";
                            }
                        }
                        //Salto de linea
                        //document.write("<br>");
                        guardar+="<br>"
                    }
                }
                //Piramide inferior
                for (var fila = 2; fila <= maximo; fila++) {
                    if (fila % 2 != 0) {
                        //Piramide de menor a mayor
                        for (var columna = 1; columna <= fila; columna++) {
                            //document.write("&nbsp;");
                            guardar+="&nbsp;";
                        }
                        //Piramide de mayor a menor
                        for (var colinf = maximo; colinf >= fila; colinf--) {
                            if (colinf == maximo || colinf == fila) {
                                //document.write("&#42;&nbsp;");
                                guardar+="&#42;&nbsp;";
                            } else if (colinf == ((maximo + fila) / 2)) {
                                //document.write("&#124;&nbsp;");
                                guardar+="&#124;&nbsp;";
                            } else {
                                //document.write("&nbsp;&nbsp;");
                                guardar+="&nbsp;&nbsp;";
                            }
                        }
                        //Salto de linea
                        //document.write("<br>");
                        guardar+="<br>";
                    }
                }

            }
            document.getElementById('resultado').innerHTML='Rombo con el numero '+guardar;
        }
    </script>
    <form>
        <p>Enter a number between 0 to 9</p>
        <input type="text" id="numero" name="numero" onkeyup="mifig()">
    </form>
    <div id="resultado"></div>
</body>

</html>"}]],"markups":[],"sections":[[10,0],[1,"p",[]]]}

